    #%%%%%%%%%%%%%%% NONLINEAR ELEMENTS %%%%%%%%%%%%%%%

    @staticmethod
    def eEHX(self, nargout,*args):

        import numpy as np
        EV_  = args[0]
        iel_ = args[1]
        U_ = np.zeros((3,4))
        IV_ = np.zeros(3)
        U_[1,0] = U_[1,0]+1
        U_[1,2] = U_[1,2]-1
        U_[0,1] = U_[0,1]+1
        U_[2,3] = U_[2,3]+1
        IV_[0] = U_[0:1,:].dot(EV_)
        IV_[1] = U_[1:2,:].dot(EV_)
        IV_[2] = U_[2:3,:].dot(EV_)
        T = IV_[1]/IV_[2]
        CT = np.cos(T)
        ST = np.sin(T)
        CCSQ = IV_[2]*IV_[2]
        CCCB = CCSQ*IV_[2]
        ACT = IV_[0]*CT
        AST = IV_[0]*ST
        f_   = ACT
        if not isinstance( f_, float ):
            f_   = f_.item();
        if nargout>1:
            try:
                dim = len(IV_)
            except:
                dim = len(EV_)
            g_ = np.zeros(dim)
            g_[0] = CT
            g_[1] = -AST/IV_[2]
            g_[2] = AST*IV_[1]/CCSQ
            g_ =  U_.T.dot(g_)
            if nargout>2:
                H_ = np.zeros((3,3))
                H_[0,1] = -ST/IV_[2]
                H_[1,0] = H_[0,1]
                H_[0,2] = ST*IV_[1]/CCSQ
                H_[2,0] = H_[0,2]
                H_[1,1] = -ACT/CCSQ
                H_[1,2] = (AST+IV_[1]*ACT/IV_[2])/CCSQ
                H_[2,1] = H_[1,2]
                H_[2,2] = -IV_[1]*(IV_[1]*ACT/IV_[2]+AST+AST)/CCCB
                H_ = U_.T.dot(H_).dot(U_)
        if nargout == 1:
            return f_
        elif nargout == 2:
            return f_,g_
        elif nargout == 3:
            return f_,g_,H_

    #%%%%%%%%%%%%%%%%% NONLINEAR GROUPS  %%%%%%%%%%%%%%%

    @staticmethod
    def gL2(self,nargout,*args):

        GVAR_ = args[0]
        igr_  = args[1]
        f_= GVAR_*GVAR_
        if nargout>1:
            g_ = GVAR_+GVAR_
            if nargout>2:
                H_ = np.zeros((1,1))
                H_ = 2.0
        if nargout == 1:
            return f_
        elif nargout == 2:
            return f_,g_
        elif nargout == 3:
            return f_,g_,H_

